mapper.insert:
insert into Task(
TaskID,TaskType,Grade,
SourPosiID,DestPosiID,Dist,
Deadline,TStat,CreatDate,UserID,IsDelete
)values (
#{TaskID},#{TaskType},#{Grade},
#{SourPosiID},#{DestPosiID},#{Dist},
#{Deadline},#{TStat},#{CreatDate},#{UserID},#{IsDelete}
)

mapper.update:
update
Task
set
TaskType=#{TaskType}
where
TaskID=#{TaskID}
